$(function(){$(".bxd-i>div").click(function(e){e.preventDefault();$(this).toggleClass("bxd-o");var elems=$(this).next().children().children("p");elems.each(function(){if($(this).is(":visible")&&(parseInt($(this)[0].scrollWidth)-20)>$(this).width()&&$(this).next().length==0){$(this).after("<a></a>");$(this).parent().css("cursor","pointer");$(this).parent().click(function(e){var elem=$(this).children("p");$(elem).toggleClass("bxd-c");});}});});});