<?global $TEMPLATE_OPTIONS;global $SKIP_JS_STORES;?><?if(!$SKIP_JS_STORES):?>	<?$SKIP_JS_STORES=true;?>	<script type="text/javascript">	$(document).ready(function() {		var bStores = (arOptimusOptions["THEME"]["STORES_SOURCE"]!= 'iblock' ? 'Y' : 'N');		$('.stores_text_wrapp a').each(function() {			var href = '';						if (bStores=="Y"){				if((href = $(this).attr('data-storehref'))){					$(this).attr('href', href)				}			}			else{				if((href = $(this).attr('data-iblockhref'))){					$(this).attr('href', href)				}			}		});	});	</script><?endif;?>