<?if(!defined("B_PROLOG_INCLUDED") || B_PROLOG_INCLUDED!==true)	die();$wizard =& $this->GetWizard();if(!CModule::IncludeModule("iblock") || !CModule::IncludeModule("mlife.asz"))	return;if(COption::GetOptionString("mlife.asz", "wizard_installed", "N", WIZARD_SITE_ID) == "Y" && !WIZARD_INSTALL_DEMO_DATA){	return;}$arFields = Array(	"CODE" => $wizard->GetVar("catalogCurency"),	"BASE" => "Y",	"CURS" => "1.00",	"SITEID" => WIZARD_SITE_ID);$baseCheck = \Mlife\Asz\CurencyTable::getList(	array(		'select' => array('ID'),		'filter' => array("CODE"=>$wizard->GetVar("catalogCurency"),"SITEID"=>WIZARD_SITE_ID),		'limit' => 1,	));if(!$baseCheck->Fetch()){	$res = \Mlife\Asz\CurencyTable::add($arFields);}