<?if(!defined("B_PROLOG_INCLUDED") || B_PROLOG_INCLUDED!==true)	die();$wizard =& $this->GetWizard();if(!CModule::IncludeModule("iblock") || !CModule::IncludeModule("mlife.asz"))	return;if(COption::GetOptionString("mlife.asz", "wizard_installed", "N", WIZARD_SITE_ID) == "Y" && !WIZARD_INSTALL_DEMO_DATA){	return;}WizardServices::IncludeServiceLang("pay.php", "ru");$paysystem = $wizard->GetVar("paysystem");if($paysystem["cash"]=="Y"){		$baseCheck = \Mlife\Asz\PaysystemTable::getList(			array(				'select' => array('ID'),				'filter' => array("SITEID"=>WIZARD_SITE_ID),				'limit' => 1,			)		);	if(!$baseCheck->Fetch()){		$arFields = Array(			"SITEID" => WIZARD_SITE_ID,			"NAME" => GetMessage("MLIFE_ASZ_WZ_PAY_1"),			"ACTIVE" => "Y",			"ACTIONFILE" => "paydefault",			"PARAMS" => "image=/bitrix/images/mlife/asz/deliver.png|delivery=all|",			"DESC" => "",		);		$res = \Mlife\Asz\PaysystemTable::add($arFields);	}}