<?php/** * Bitrix Framework * @package    Bitrix * @subpackage mlife.asz * @copyright  2014 Zahalski Andrew */namespace Mlife\Asz;use Bitrix\Main\Entity;use Bitrix\Main\Localization\Loc;Loc::loadMessages(__FILE__);class ElementTable extends Entity\DataManager{	public static function getFilePath()	{		return __FILE__;	}	public static function getTableName()	{		return 'b_iblock_element';	}	public static function getMap()	{		return array(			new Entity\IntegerField('ID', array(				'primary' => true,				'autocomplete' => true,				)			),			new Entity\StringField('NAME', array(				'required' => true,				)			),			new Entity\StringField('CODE', array(				'required' => true,				)			),			new Entity\StringField('PREVIEW_TEXT', array(				'required' => true,				)			),			new Entity\StringField('DETAIL_TEXT', array(				'required' => true,				)			),			new Entity\IntegerField('IBLOCK_ID', array(				'required' => true,				)			),			new Entity\ReferenceField('IBLOCK', 'Iblock', 				array('=this.IBLOCK_ID' => 'ref.ID')			),			new Entity\BooleanField('ACTIVE', array(				'required' => false,				'values' => array('N', 'Y'),				)			),			new Entity\IntegerField('IBLOCK_SECTION_ID', array(				'required' => true,				)			),			new Entity\ReferenceField('SECTION', '\Mlife\Asz\ElementSectionTable', 				array('=this.ID' => 'ref.IBLOCK_ELEMENT_ID')			),			new Entity\IntegerField('DETAIL_PICTURE', array(				'required' => true,				)			),			new Entity\ReferenceField('PROPERTY', '\Mlife\Asz\PropertyValuesTable', 				array('=this.ID' => 'ref.IBLOCK_ELEMENT_ID')			),			new Entity\ReferenceField('PRICE', '\Mlife\Asz\PriceTable', 				array('=this.ID' => 'ref.PRODID')			),			new Entity\ReferenceField('KOL', '\Mlife\Asz\QuantTable', 				array('=this.ID' => 'ref.PRODID')			),		);	}}