<?php/** * Bitrix Framework * @package    Bitrix * @subpackage mlife.asz * @copyright  2014 Zahalski Andrew */namespace Mlife\Asz;use Bitrix\Main\Entity;use Bitrix\Main\Localization\Loc;Loc::loadMessages(__FILE__);class MetafilterTable extends Entity\DataManager{	public static function getFilePath()	{		return __FILE__;	}	public static function getTableName()	{		return 'mlife_asz_metafilter';	}	public static function getMap()	{		return array(			new Entity\IntegerField('ID', array(				'primary' => true,				'autocomplete' => true,				)			),			new Entity\IntegerField('IBLOCKID', array(				'required' => true,				)			),			new Entity\IntegerField('SORT', array(				'required' => false,				)			),			new Entity\StringField('TEMPLATE_TITLE', array(				'required' => false,				)			),			new Entity\StringField('TEMPLATE_KEY', array(				'required' => false,				)			),			new Entity\StringField('TEMPLATE_DESC', array(				'required' => false,				)			),			new Entity\StringField('TEMPLATE_NAME', array(				'required' => false,				)			),			new Entity\StringField('TEMPLATE_TEXT', array(				'required' => false,				)			),			new Entity\ReferenceField('SITE', '\Bitrix\Iblock\IblockSiteTable', 				array('=this.IBLOCKID' => 'ref.IBLOCK_ID')			),		);	}	}?>