function countdown_dashboard(){$(".countdown_dashboard").each(function(){var h=$(this).data("year");var e=$(this).data("month")-1;var i=$(this).data("day");var j=$(this).data("hours");var b=$(this).data("minutes");var l=$(this).data("seconds");var f=new Date(h,e,i,j,b,l);var a=new Date();var k=f-a;var d=$.trim(Math.floor(k/1000/60/60/24));var c=$.trim(Math.floor((k-d*24*60*60*1000)/1000/60/60));var g=$.trim(Math.floor((k-d*24*60*60*1000-c*60*60*1000)/1000/60));if(d.length==0){d="00"}if(d.length==1){d="0"+d}if(c.length==0){c="00"}if(c.length==1){c="0"+c}if(g.length==0){g="00"}if(g.length==1){g="0"+g}$(this).find(".days_dash .digit").eq(0).text(parseInt(d.substr(0,d.length-1)));$(this).find(".days_dash .digit").eq(1).text(parseInt(d.substr(d.length-1,1)));$(this).find(".hours_dash .digit").eq(0).text(parseInt(c.substr(0,c.length-1)));$(this).find(".hours_dash .digit").eq(1).text(parseInt(c.substr(c.length-1,1)));$(this).find(".minutes_dash .digit").eq(0).text(parseInt(g.substr(0,g.length-1)));$(this).find(".minutes_dash .digit").eq(1).text(parseInt(g.substr(g.length-1,1)))})}jQuery(document).ready(function(){setInterval(countdown_dashboard,1000)});