function serialize(c){if(!c||c.nodeName!=="FORM"){return}var b,a,d=[];for(b=c.elements.length-1;b>=0;b=b-1){if(c.elements[b].name===""){continue}switch(c.elements[b].nodeName){case"INPUT":switch(c.elements[b].type){case"text":case"hidden":case"password":case"button":case"reset":case"submit":d.push(c.elements[b].name+"="+encodeURIComponent(c.elements[b].value));break;case"checkbox":case"radio":if(c.elements[b].checked){d.push(c.elements[b].name+"="+encodeURIComponent(c.elements[b].value))}break;case"file":break}break;case"TEXTAREA":d.push(c.elements[b].name+"="+encodeURIComponent(c.elements[b].value));break;case"SELECT":switch(c.elements[b].type){case"select-one":d.push(c.elements[b].name+"="+encodeURIComponent(c.elements[b].value));break;case"select-multiple":for(a=c.elements[b].options.length-1;a>=0;a=a-1){if(c.elements[b].options[a].selected){d.push(c.elements[b].name+"="+encodeURIComponent(c.elements[b].options[a].value))}}break}break;case"BUTTON":switch(c.elements[b].type){case"reset":case"submit":case"button":d.push(c.elements[b].name+"="+encodeURIComponent(c.elements[b].value));break}break}}return d.join("&")};