function shelveInit(){$("a[data-action=addToShelve]").unbind("click");$("a[data-action=addToShelve]").click(function(){var b=$(this).hasClass("ADDED_SHELVE_PRODUCT");var a=$("#SHELVE_PRODUCT").attr("data-elem-id");$.post(JAVASCRIPT_SITE_DIR+"include/ajax/add2basket.php","ADD2BASKET=1&elemId="+a+"&act=addToShelve",function(c){$("#box-shelve, #box-shelve .message-demo, .DIV_SHELVE_PRODUCT").hide();if(c.status=="OK"){if(b){$("#box-shelve, #box-shelve .added-success, .DELETED_SHELVE_PRODUCT").show()}else{$("#box-shelve, #box-shelve .deleted-success, .ADDED_SHELVE_PRODUCT").show()}}else{if(b){$("#box-shelve, #box-shelve .added-error, .ADDED_SHELVE_PRODUCT").show()}else{$("#box-shelve, #box-shelve .deleted-error, .DELETED_SHELVE_PRODUCT").show()}}setTimeout(function(){$("#box-shelve").hide()},2500)},"JSON");return false})}$(document).ready(function(){shelveInit()});$(document).ajaxComplete(function(){shelveInit()});