$(document).ready(function(){$(".tree > ul").attr("role","tree").find("ul").attr("role","group");$(".tree").find("li:has(ul)").addClass("parent_li").attr("role","treeitem").find("span.collapse").attr("title","Collapse this branch").on("click",function(g){var f=$(this).parent().parent("li.parent_li").find(" > ul > li");if(f.is(":visible")){f.hide("fast");$(this).attr("title","Expand this branch").find(" > i").addClass("icon-plus-sign").removeClass("icon-minus-sign")}else{f.show("fast");$(this).attr("title","Collapse this branch").find(" > i").addClass("icon-minus-sign").removeClass("icon-plus-sign")}g.stopPropagation()});if($("#open-section").length){var c=$("#open-section");var d=c.parents("li.parent_li");var a=[];d.each(function(f,e){a[$(e).attr("data-level")]=($(e))});for(var b in a){if(b>0){a[b].find("span.collapse:first").click()}}$("#open-section span.collapse:first").click()}});