$.validator.setDefaults({highlight:function(d,a,c){var b;if(d.type==="radio"){b=this.findByName(d.name)}else{b=$(d)}b.addClass(a).removeClass(c);b.closest("div.control-group").addClass("error")},unhighlight:function(d,a,c){var b;if(d.type==="radio"){b=this.findByName(d.name)}else{b=$(d)}b.removeClass(a).addClass(c);if(b.closest("div.control-group").find(".error").length==0){b.closest("div.control-group").removeClass("error")}}});$(function(){$(".field-validation-valid, .field-validation-error").each(function(){var a=$(this);if(!a.hasClass("help-inline")||!a.hasClass("help-block")){if(a.is("p")){a.addClass("help-block")}else{if(a.is("span")){a.addClass("help-inline")}else{a.addClass("help-block")}}}});$("form").each(function(){$(this).find("div.control-group").each(function(){if($(this).find(".field-validation-error").length>0){$(this).addClass("error")}})})});