jQuery(function(){var a=$("form[name=COMMENTS]");a.submit(function(){$("#form-loading-comments").fadeIn();$("#error-comments, #success-comments, #beforesend-comments").hide();if(b()){c()}else{$("#form-loading-comments").hide();$("#beforesend-comments, #results-comments").fadeIn()}$("input, select, textarea, button",a).blur();return false});function b(){var d=[];$(".req",a).each(function(){if(!$(this).val()){d.push(1);$(this).addClass("error")}else{$(this).removeClass("error")}});if(d.length===0){return true}else{return false}}function c(){$.ajax({type:"POST",url:a.attr("action"),dataType:"json",data:a.serialize(),success:function(d){$("#form-loading-comments").hide();$("input, textarea",a).removeClass("error");if(d.MESSAGE.ERROR<1){$("#results-comments, #success-comments").fadeIn();$("input, select, textarea",a).not("[type=hidden], [type=submit]").val("")}else{$("#results-comments, #error-comments").hide().fadeIn()}},error:function(d){$("#form-loading-comments").hide();$("#results-comments, #error-comments").hide().fadeIn()}});return false}});