function QB_get_timer(d){var a=new Date;a=(Date.parse(a))/1000;var e=d-a;var h=parseInt((e/(60*60))/24);if(h<10){h="0"+h}h=h.toString();var b=parseInt((e/(60*60))%24);if(b<10){b="0"+b}b=b.toString();var c=parseInt(e/(60))%60;if(c<10){c="0"+c}c=c.toString();var f=parseInt(e)%60;if(f<10){f="0"+f}f=f.toString();var g={days:h,hours:b,minutes:c,seconds:f};return g}function QB_timer(c){c.addClass("inited");time=QB_get_timer(c.find(".digits").data("dateto"));c.find(".js-days").html(time.days).data("time",time.days);c.find(".js-hours").html(time.hours).data("time",time.hours);c.find(".js-minutes").html(time.minutes).data("time",time.minutes);c.find(".js-seconds").html(time.seconds).data("time",time.seconds);if(c.find(".js-seconds").length!=0){setInterval(function(){var e=parseInt(c.find(".js-hours").data("time"));var f=parseInt(c.find(".js-minutes").data("time"));var g=parseInt(c.find(".js-seconds").data("time"));g--;if(g<0){g=59;f--}if(f<0){f=59;e--}if(g<10){c.find(".js-seconds").html("0"+g)}else{if(g>=10){c.find(".js-seconds").html(g)}}if(f<10){c.find(".js-minutes").html("0"+f)}else{if(f>=10){c.find(".js-minutes").html(f)}}if(e<10){c.find(".js-hours").html("0"+e)}else{if(e>=10){c.find(".js-hours").html(e)}}c.find(".js-seconds").data("time",g).attr("data-time",g);c.find(".js-minutes").data("time",f).attr("data-time",f);c.find(".js-hours").data("time",e).attr("data-time",e)},1000)}else{var d=c.find(".js-days").data("time");var a=c.find(".js-hours").data("time");var b=c.find(".js-minutes").data("time");if(b<10){c.find(".js-minutes").html("0"+b)}else{if(b>=10){c.find(".js-minutes").html(b)}}if(a<10&&a.charAt(0)!="0"){c.find(".js-hours").html("0"+a)}else{if(a>=10){c.find(".js-hours").html(a)}}if(d<10){c.find(".js-days").html("0"+d)}else{if(d>=10){c.find(".js-days").html(d)}}}};