function RSGOPRO_SetHeight(){setTimeout(function(){$(".mainsections").find("li.section").css("minHeight",0);var a=$(".mainsections").find("li.section:first").offset().top;var c=0;var b=0;$(".mainsections").find("li.section").each(function(d){if($(this).offset().top!=a){if(c>0){$(".mainsections").find("li.section:lt("+(d)+"):gt("+c+")").css("minHeight",b)}else{$(".mainsections").find("li.section:lt("+(d)+")").css("minHeight",b)}c=(d-1);a=$(this).offset().top;b=$(this).outerHeight(true)+2}else{if($(this).outerHeight(true)>b){b=$(this).outerHeight(true)+2}}});if(c>0){$(".mainsections").find("li.section:gt("+c+")").css("minHeight",b)}else{$(".mainsections").find("li.section").css("minHeight",b)}},100)}$(document).ready(function(){RSGOPRO_SetHeight();$(window).bind("resize",RSGOPRO_SetHeight);$(window).load(function(){RSGOPRO_SetHeight();setTimeout(function(){RSGOPRO_SetHeight()},50)})});