jQuery(function(){$(".filter").jScrollPane({mouseWheelSpeed:30,showArrows:true,verticalDragMinHeight:54,verticalDragMaxHeight:54,contentWidth:225});$(".filter-container .slider").slider({min:0,max:20000,values:[0,20000],animate:"normal",range:true,create:function(b,c){var a=$(b.target);$(this).find(".ui-slider-handle").each(function(){var d=$("<div />").appendTo($(this));if($(this).data("uiSliderHandleIndex")==0){d.addClass("left");d.html(a.slider("values",0))}else{d.addClass("right");d.html(a.slider("values",1))}})},slide:function(a,b){$(this).find(".ui-slider-handle").each(function(){if($(this).data("uiSliderHandleIndex")==0){$(this).children().html(b.values[0])}else{$(this).children().html(b.values[1])}})}})});