var jsvhover=function(){var c=document.getElementById("vertical-multilevel-menu");if(!c){return}var a=c.getElementsByTagName("li");for(var b=0;b<a.length;b++){a[b].onmouseover=function(){this.className+=" jsvhover"};a[b].onmouseout=function(){this.className=this.className.replace(new RegExp(" jsvhover\\b"),"")}}};if(window.attachEvent){window.attachEvent("onload",jsvhover)};